import React, {Component} from 'react'
import './createProduct.scss';
import { postProduct } from "../../../services/bakeryBE_API";

export default class CreateProduct extends Component {

  constructor(props) {
    super(props)

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeAvailability = this.onChangeAvailability.bind(this);
    this.onChangeRaf = this.onChangeRaf.bind(this);
    this.onChangeSugarFree = this.onChangeSugarFree.bind(this);
    this.onChangeGlutenFree = this.onChangeGlutenFree.bind(this);

    this.state = {
      title: '',
      description: '',
      availability: false,
      raf: false,
      sugarFree: false,
      glutenFree: false,
      products: []
    }
  }

  // const onChange = (e) => {
  //   setProdData((prevState) => ({
  //     ...prevState,
  //     [e.target.name]: e.target.value,
  //   }))
  // }

  // const onSubmit = (e) => {
  //   e.preventDefault();


  //   // console.log(prodData);

  //   postProduct(prodData).then(res => {
  //     console.log(res.data)
  //     products = [...products].push(prodData)
  //   })
  // } 

  // const { title, description,availability, raf, sugarFree,glutenFree } = prodData;
render(){
  return (
    <>
      <form onSubmit={onSubmit}>
        <label htmlFor="title">Назва</label>
        <input name="title" type="text" id="title" value={title} onChange={onChange}/>
        <label htmlFor="description">Опис</label>
        <input
          name="description"
          type="text"
          id="description"
          value={description}
          onChange={onChange}
        />
        <label htmlFor="availability">Наявність</label>
        <select name="availability" id="availability" onChange={onChange} value={availability}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <label htmlFor="raf">Раф</label>
        <select name="raf" id="raf" onChange={onChange} value={raf}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <label htmlFor="sugarFree">Цукор</label>
        <select name="sugarFree" id="sugarFree" onChange={onChange} value={sugarFree}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <label htmlFor="glutenFree">Глютен</label>
        <select name="glutenFree" id="glutenFree" onChange={onChange} value={glutenFree}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <button type="submit">Зберегти</button>
      </form>
    </>
  )
}
};
