import './item.scss';
import { useState } from 'react';

const Item = (props) => {
  const [hover, setHover] = useState(false);
  const hoverStyle = {
    "transform": "scale(1.2)",
    "transition": "1s"
  };
  const normalStyle = {
    "transition": "1s"
  }

  const onMouseEnter = () => {
    setHover(true)
  }

  const onMouseLeave = () => {
    setHover(false)
  }

  return (
    <div className="card" key={props._id}>
      <div className="title">
        <h3>{props.title}</h3>
      </div>
      <div className="photo-item" >
        <img src={props.photo-link} alt="photo-item" style={hover ? hoverStyle : normalStyle} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}/>
      </div>
      <div className="price">{props.price}</div>
      <div className="description">{props.description}</div>
    </div>

  )
}

export default Item;

