import './item.scss';

const Item = (props) => {
  const [hover, setHover] = useState(false);
  const hoverStyle = {
    "transform": "scale(1.2)",
    "transition": "1s"
  };
  const normalStyle = {
    "transition": "1s"
  }

  const onMouseEnter = () => {
    setHover(true)
  }

  const onMouseLeave = () => {
    setHover(false)
  }

  return (
    <div className="photo-item" key={props._id}>
        <img src={props.link} alt="photo-item" style={hover ? hoverStyle : normalStyle} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave} onClick={() => { openInNewTab(props.link) }}/>
    </div>
  )
}

export default Item;
