import "./adaminItem.scss";
import { Component } from "react";

const halal = require("../../../../assets/halal.png");
const raw = require("../../../../assets/raw.png");

const AdminItem = () => {

    return (
      <div className="Admin-card" key={this.props._id}>
        <div className="admin-title">
          <div>
            <b>Назва:</b> <b>{this.props.title}</b>
          </div>
        </div>
        <div className="admin-photo-item">
          {/* <img src={props.photo} alt="product" style={hover ? hoverStyle : normalStyle} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}/> */}
          <div className="admin-photo-link">
            <b>Посилання на фото:</b>
            {this.props.photo}
          </div>
          <div className="admin-labels">
            <img
              src={halal}
              alt="halal"
              style={
                this.props.halal === true
                  ? { display: "block" }
                  : { display: "none" }
              }
            />
            {/* <img src="" alt="kosher" /> */}
            {/* <img src="" alt="vegan" /> */}
            <img
              src={raw}
              alt="raw"
              style={
                this.props.raw === true ? { display: "block" } : { display: "none" }
              }
            />
            {/* <img src="" alt="glutem-free" /> */}
            {/* <img src="" alt="shugar-free" /> */}
          </div>
        </div>

        <div className="admin-price">
          <b>Ціна:</b> {this.props.price} грн.
        </div>
        <div className="admin-description">
          <b>Короткий опис:</b> {this.props.description}
        </div>
        <div className="admin-buttons">
          <button className="Delete" onSubmit={this.props.deleteProduct}>Del</button>
          <button className="edit">Edit</button>
        </div>
      </div>
    );
}


