import React, { Component } from "react";
import "./createProduct.scss";
import {postProduct} from '../../../services/bakeryBE_API';

export default class CreateProduct extends Component {
  constructor(props) {
    super(props);

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangePhoto = this.onChangePhoto.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeAvailability = this.onChangeAvailability.bind(this);
    this.onChangeRaf = this.onChangeRaf.bind(this);
    this.onChangeSugarFree = this.onChangeSugarFree.bind(this);
    this.onChangeGlutenFree = this.onChangeGlutenFree.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      title: '',
      photo: '',
      description: '',
      price: '',
      availability: false,

      halal: false,
      Kosher: false,
      vegan: false, 

      raw: false,
      sugarFree: false,
      glutenFree: false,
      products: []
    };

  }

  onChangeTitle(p) {
    this.setState({ title: p.target.value });
  }
  onChangePhoto(p){
    this.setState({photo: p.target.value});
  }
  onChangeDescription(p) {
    this.setState({ description: p.target.value });
  }
  onChangeAvailability(p) {
    this.setState({ availability: p.target.value });
  }
  onChangeRaw(p) {
    this.setState({ raf: p.target.value });
  }
  onChangePrice(p){
    this.setState({price: p.target.value})
  }
  onChangeSugarFree(p) {
    this.setState({ sugarFree: p.target.value });
  }
  onChangeGlutenFree(p) {
    this.setState({ glutenFree: p.target.value });
  }


  onSubmit(e) {
    e.preventDefault();

    const product = {
      title: this.state.title,
      photo: this.state.photo,
      description: this.state.description,
      price: this.state.price,
      availability: this.state.availability,
      halal: this.state.halal,
      kosher: this.state.kosher,
      vegan: this.state.vegan, 
      raf: this.state.raf,
      sugarFree: this.state.sugarFree,
      glutenFree: this.state.glutenFree,
    };

    console.log(product)

    postProduct(product)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });

  }

  render() {
    return (
      <>
        <h2>Добавить продукт в магазин</h2>
        <form className="add-product">
          <label htmlFor="title">Назва</label>
          <input
            name="title"
            type="text"
            id="title"
            value={this.state.title}
            onChange={this.onChangeTitle}
          />
          <label htmlFor="photo-link">Фото</label>
          <input
            name="photo-link"
            type="text"
            id="photo-link"
            value={this.state.photo}
            onChange={this.onChangePhoto}
          />
          <label htmlFor="description">Опис</label>
          <textarea
            rows="8"
            name="description"
            type="text"
            id="description"
            value={this.state.description}
            onChange={this.onChangeDescription}
          />
          <label htmlFor="price">Ціна</label>
          <input type="text" name="price" id="price" value={this.state.price} onChange={this.onChangePrice}/>
          <label htmlFor="availability">Наявність</label>
          <select
            name="availability"
            id="availability"
            onChange={this.onChangeAvailability}
            value={this.state.availability}
          >
            <option value="true">Так</option>
            <option value="false">
              Ні
            </option>
          </select>
          <label htmlFor="raf">Раф</label>
          <select name="raf" id="raf" onChange={this.onChangeRaf} value={this.state.raf}>
            <option value="true">Так</option>
            <option value="false">
              Ні
            </option>
          </select>
          <label htmlFor="sugarFree">Без цукру</label>
          <select
            name="sugarFree"
            id="sugarFree"
            onChange={this.onChangeSugarFree}
            value={this.state.sugarFree}
          >
            <option value="true">Так</option>
            <option value="false">
              Ні
            </option>
          </select>
          <label htmlFor="glutenFree">Без-лютену</label>
          <select
            name="glutenFree"
            id="glutenFree"
            onChange={this.onChangeGlutenFree}
            value={this.state.glutenFree}
          >
            <option value="true">Так</option>
            <option value="false">
              Ні
            </option>
          </select>
          <button type="submit" onClick={this.onSubmit}>Зберегти</button>
        </form>
      </>
    );
  }
}
