import { useState } from "react";
import './createProduct.scss';
import { postProduct } from "../../../services/bakeryBE_API";

const CreateProduct = () => {
  const [prodData, setProdData] = useState({
    title: "",
    description: "",
    availability: false,
    raf: false,
    sugarFree: false,
    glutenFree: false,
  });
  const [products, setProducts] = useState([]);
  const onChange = (e) => {
    setProdData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }))
  }

  const onSubmit = (e) => {
    e.preventDefault();
    const product = {
      title: this.state.title,
      description: this.state.description,
    }
    console.log(product);

    postProduct(product).then(res => {
      console.log(res.data)
    })
  } 

  const { title, description, availability, raf, sugarFree, glutenFree } =
    prodData;

  return (
    <>
      <form onSubmit={onSubmit}>
        <label htmlFor="title">Назва</label>
        <input name="title" type="text" id="title" value={title} onChange={onChange}/>
        <label htmlFor="description">Опис</label>
        <input
          name="description"
          type="text"
          id="description"
          value={description}
          onChange={onChange}
        />
        <label htmlFor="availability">Наявність</label>
        <select name="availability" id="availability" onChange={onChange}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <label htmlFor="raf">Раф</label>
        <select name="raf" id="raf" onChange={onChange}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <label htmlFor="sugarFree">Цукор</label>
        <select name="sugarFree" id="sugarFree" onChange={onChange}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <label htmlFor="glutenFree">Глютен</label>
        <select name="glutenFree" id="glutenFree" onChange={onChange}>
          <option value="true">true</option>
          <option selected value="false">false</option>
        </select>
        <button type="submit">Зберегти</button>
      </form>
    </>
  );
};
export default CreateProduct;
